mapper.select:
select user_id as Id, username, password, email, full_name, bio, profile_image_url as profileImageUrl
from user
where user_id = #{id}

mapper.update:
UPDATE user
email = #{userParam.email}, password = #{userParam.password}, full_name = #{userParam.fullname}, bio = #{userParam.bio}, profile_image_url = #{userParam.profileImageUrl}
WHERE user_id = #{user.Id}

mapper.select:
